import { useReducer } from "react";
import "../Products/products.css"
import { TYPES } from "../ShoppingAction";
import {
  shoppingInitialState,
  shoppingReducer,
} from "../ShoppingReducer";
import ProductItem from "../ProductItem";
import CartItem from "../CartItem";
import Button from '../Components/ButtonFilter';

const allCategories = ['All', ...new Set(products.map(product => product.category))];

console.log(allCategories);


const Products = () => {
	const [state, dispatch] = useReducer(shoppingReducer, shoppingInitialState);

  const { products, cart } = state;

  const addToCart = (id) => {
    //console.log(id);
    dispatch({ type: TYPES.ADD_TO_CART, payload: id });
  };

  const delFromCart = (id, all = false) => {
    //console.log(id, all);
    if (all) {
      dispatch({ type: TYPES.REMOVE_ALL_FROM_CART, payload: id });
    } else {
      dispatch({ type: TYPES.REMOVE_ONE_FROM_CART, payload: id });
    }
  };

  const clearCart = () => {
    dispatch({ type: TYPES.CLEAR_CART });
  };

	return (

      <>
			<h1 className="first-text">PRODUCTOS</h1>
			{products.map((product) => (
				<ProductItem key={product.id} data={product} addToCart={addToCart} />
			))}
			<h1 className="first-text-cart">CARRITO</h1>

				{cart.map((item, index) => (
					<CartItem key={index} data={item} delFromCart={delFromCart} addToCart={addToCart} />
				))}
			</>

	);
}

export default Products;
