import React, { useState } from 'react';
import "../Cart/cart.css"
import { useSelector, useDispatch } from "react-redux";

const Cart = () => {
  const [url, setUrl] = useState("");
  const message = () => {
    let text = "https://wa.me/573222534986?text="
    text += `Bienvenido a Persky, hemos visto que estas interesado en los siguientes productos: \n\n`
    cart.forEach(item => {
        text += `${item.name}\n`
        text += `Talla: ${item.price}\n`
        text += `Color:  ${item.description}\n`
        text += `Cantidad: ${item.quantity}\n`
        text += `Precio: ${item.price.toFixed(3)}\n\n`
    })
    text += `\nEl valor total de mi compra es: $${total.toFixed(3)}`
    text += `\nGracias por comprar en Persky, nos pondremos en contacto contigo muy pronto para terminar tu compra\n`
    setUrl(encodeURI(text))

}
  const cart = useSelector((state) => state);
  console.log(cart);
  const dispatch = useDispatch();
  const addition = (acc, currentvalue) => {
    return acc + currentvalue.price * currentvalue.quantity;
  };
  const total = cart.reduce(addition, 0);
  return (
    <>
    {cart.length > 0 && <h1 className="first-text">CARRITO DE COMPRAS</h1>}
    {cart.map((item) => {
      return (
		<div className="container-cart">
      <div className="card-cart" key={item.id}>
					<div className="left-column-cart background1-left-column-cart">

						<img src={`${process.env.PUBLIC_URL}/img/${item.img}`} alt={item.name} width="150px" className="img-fluid img-cart"/>
						<h6>{item.name}</h6>
						<h6>${item.price}.00 x {item.quantity} = ${item.price * item.quantity}.00</h6>
					</div>

					<div className="right-column-cart">
						<div>
							<h4>Descripción</h4>

						</div>
						<p>{item.description}</p>
						<div className="right-column-btn">
						<button type="button" onClick={() => {
                    if (item.quantity > 1) {
                      dispatch({ type: "DECREASE", payload: item });
                    } else {
                      dispatch({ type: "REMOVE", payload: item });
                    }
                  }} className="button-cart background1-left-column "><h1>-</h1></button>
						<button type="button" onClick={() => dispatch({ type: "INCREASE", payload: item })} className="button-cart background1-left-column"><h1>+</h1></button>
						</div>
					</div>

				</div>

				</div>
         );
      })}
      {cart.length === 0 && <div className="container-home">
      {cart.length === 0 && <h1 className="first-text">CARRITO DE COMPRAS</h1>}
     {cart.length === 0 && <h1 className="second-text">TU CARRITO DE COMPRAS ESTA VACIO:(<br/>
     AÑADE ALGUN PRODUCTO!</h1>}
        </div>}
        <div>
        {total > 0 && <h2 className='btn-cart'>total:{total}.000</h2>}
        {total > 0 && <a href={url} message={ message} className='btn-what'>Hacer Pedido</a>}
        </div>
      </>
	);
};

export default Cart;
