import { useReducer, useState } from "react";
import "../Products/products.css"
import { TYPES } from "../ShoppingAction";
import {
  shoppingInitialState,
  shoppingReducer,
} from "../ShoppingReducer";
import ProductItem from "../ProductItem";
import CartItem from "../CartItem";
import Button from '../Components/ButtonFilter';


const Products = () => {
  const [state, dispatch] = useReducer(shoppingReducer, shoppingInitialState);

  const { products, cart, cartItems } = state;

  const itemsPrice = cartItems.reduce((a, c) => a + c.qty * c.price, 0);
  const totalPrice = itemsPrice;

  const addToCart = (id) => {
    dispatch({ type: TYPES.ADD_TO_CART, payload: id });
  };

  const delFromCart = (id, all = false) => {
    if (all) {
      dispatch({ type: TYPES.REMOVE_ALL_FROM_CART, payload: id });
    } else {
      dispatch({ type: TYPES.REMOVE_ONE_FROM_CART, payload: id });
    }
  };

  const allCategories = ['Todos', ...new Set(products.map(product => product.category))];

  const [productItem, setProductItem] = useState(products);
  const [buttons, setButtons] = useState(allCategories);

  const filter = (button) =>{

    if(button === 'Todos'){
      setProductItem(products);
      return;
    }

    const filteredData = products.filter(product => product.category ===  button);
    setProductItem(filteredData)
  }

  return (

    <>
      <h1 className="first-text">PRODUCTOS</h1>
      <Button button={buttons} filter={filter} />
      {productItem.map((product) => (
        <ProductItem key={product.id} data={product} addToCart={addToCart}  productItem={productItem}/>
      ))}
      <h1 className="first-text-cart">CARRITO</h1>

      {cart.map((item, index) => (
        <CartItem key={index} data={item} delFromCart={delFromCart} addToCart={addToCart} />
      ))}
       <div className="row">
              <div className="col-2">
                <strong>Total Price</strong>
              </div>
              <div className="col-1 text-right">
                <strong>${totalPrice.toFixed(2)}</strong>
              </div>
            </div>
    </>

  );
}

export default Products;
