import "../Pages/Cart/cart.css"

const CartItem = ({ data, delFromCart }) => {
    let { id, name, price, size, description, img, quantity } = data;
  
    return (
		<><div>
			<h1 className="first-text-cart">CARRITO</h1>
		</div><div className="container-cart">
				<div className="card-cart" key={id}>
					<div className="left-column-cart background1-left-column-cart">

						<img src={`${process.env.PUBLIC_URL}/img/${img}`} alt={name} width="150px" className="img-fluid img-cart" />
						<h6>{name}</h6>
						<h6>${price}.00 x {quantity} = ${price * quantity}.00</h6>
						<h6>{size}</h6>
					</div>

					<div className="right-column-cart">
						<div>
							<h4>Descripci√≥n</h4>

						</div>
						<p>{description}</p>
						<button onClick={() => delFromCart(id)} className="button-cart background1-left-column-cart">Eliminar</button>
					</div>

				</div>
			</div></>

    );
  };
  
  export default CartItem;
