import "../Pages/Cart/cart.css"

const CartItem = ({ data, delFromCart }) => {
    let { id, name, price, size, description, img, quantity } = data;
  
    return (
		<div className="container">
			<h1 className="first-text-cart">CARRITO</h1>
      <div className="card" key={id}>
					<div className="left-column background1-left-column">

						<img src={`${process.env.PUBLIC_URL}/img/${img}`} alt={name} width="150px" className="img-fluid img"/>
						<h6>{name}</h6>
						<h6>${price}.00 x {quantity} = ${price * quantity}.00</h6>
						<h6>{size}</h6>
					</div>

					<div className="right-column">
						<div>
							<h4>Descripci√≥n</h4>

						</div>
						<p>{description}</p>
						<button onClick={() => delFromCart(id)} className="button background1-left-column">Eliminar</button>
					</div>

				</div>
				</div>

    );
  };
  
  export default CartItem;
