import "../../src/Pages/Products/products.css"

const ProductItem = ({ data, addToCart }) => {
  let { id, name, price, size, description, img } = data;

	return (
		
		<div className="container">
				<div className="card" key={id}>
					<div className="left-column background1-left-column">

						<img src={`${process.env.PUBLIC_URL}/img/${img}`} alt={name} width="150px" className="img-fluid img"/>
						<h6>{name}</h6>
						<h6>${price}</h6>
						<h6>{size}</h6>
					</div>

					<div className="right-column">
						<div>
							<h4>Descripci√≥n</h4>

						</div>
						<p>{description}</p>
						<button onClick={() => addToCart(id)} className="button background1-left-column"><h1>+</h1></button>
						<button onClick={() => addToCart(id)} className="button background1-left-column"><h1>+</h1></button>

					</div>

				</div>

		</div>
	);
  };
  
  export default ProductItem;
