import { TYPES } from "./ShoppingAction";

export const shoppingInitialState = {
  products: [
    {
      "id": "1",
      "category": "Hombre",
      "name": "Air Force 1",
      "price": "80.000",
      "size": "32-40",
      "description": "Zapato comodo para el uso diario",
      "url": "air-force-one",
      "img": "air-force-one.png"
    },

    {
      "id": "2",
      "category": "Mujer",
      "name": "Adidas",
      "price": "80.000",
      "size": "32-42",
      "description": "Zapato comodo para el uso diario",
      "url": "adidas",
      "img": "adidas.png"
    },

    {
      "id": "3",
      "category": "Hombre",
      "name": "Air Force 1",
      "price": "80.000",
      "size": "32-40",
      "description": "Zapato comodo para el uso diario",
      "url": "air-force-one",
      "img": "air-force-one.png"
    },

    {
      "id": "4",
      "category": "Mujer",
      "name": "Adidas",
      "price": "80.000",
      "size": "32-42",
      "description": "Zapato comodo para el uso diario",
      "url": "adidas",
      "img": "adidas.png"
    },

    {
      "id": "5",
      "category": "Hombre",
      "name": "Air Force 1",
      "price": "80.000",
      "size": "32-40",
      "description": "Zapato comodo para el uso diario",
      "url": "air-force-one",
      "img": "air-force-one.png"
    },

    {
      "id": "6",
      "category": "Mujer",
      "name": "Adidas",
      "price": "80.000",
      "size": "32-42",
      "description": "Zapato comodo para el uso diario",
      "url": "adidas",
      "img": "adidas.png"
    },

    {
      "id": "7",
      "category": "Hombre",
      "name": "Air Force 1",
      "price": "80.000",
      "size": "32-40",
      "description": "Zapato comodo para el uso diario",
      "url": "air-force-one",
      "img": "air-force-one.png"
    },

    {
      "id": "8",
      "category": "Mujer",
      "name": "Adidas",
      "price": "80.000",
      "size": "32-42",
      "description": "Zapato comodo para el uso diario",
      "url": "adidas",
      "img": "adidas.png"
    }

  ],
  cart: [],
};

export function shoppingReducer(state, action) {
  switch (action.type) {
    case TYPES.ADD_TO_CART: {
      let newItem = state.products.find(
        (product) => product.id === action.payload
      );
      //console.log(newItem);

      let itemInCart = state.cart.find((item) => item.id === newItem.id);

      return itemInCart
        ? {
          ...state,
          cart: state.cart.map((item) =>
            item.id === newItem.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          ),
        }
        : {
          ...state,
          cart: [...state.cart, { ...newItem, quantity: 1 }],
        };
    }
    case TYPES.REMOVE_ONE_FROM_CART: {
      let itemToDelete = state.cart.find((item) => item.id === action.payload);

      return itemToDelete.quantity > 1
        ? {
          ...state,
          cart: state.cart.map((item) =>
            item.id === action.payload
              ? { ...item, quantity: item.quantity - 1 }
              : item
          ),
        }
        : {
          ...state,
          cart: state.cart.filter((item) => item.id !== action.payload),
        };
    }
    case TYPES.REMOVE_ALL_FROM_CART: {
      return {
        ...state,
        cart: state.cart.filter((item) => item.id !== action.payload),
      };
    }
    case TYPES.CLEAR_CART:
      return shoppingInitialState;
    default:
      return state;
  }
}
