import { useReducer, useState, useEffect } from "react";
import "../Products/products.css"
import { TYPES } from "../ShoppingAction";
import {
  shoppingInitialState,
  shoppingReducer,
} from "../ShoppingReducer";
import ProductItem from "../ProductItem";
import CartItem from "../CartItem";

function useShoes() {

	const [shoes, setShoes] = useState([])

	useEffect(() => {
		fetch("json/shoes.json")
			.then(response => response.json())
			.then(datos => {
				setShoes(datos)
			})
	}, [])

	return shoes
}

const Products = () => {
	const shoes = useShoes()
	const [state, dispatch] = useReducer(shoppingReducer, shoppingInitialState);

  const { products, cart } = state;

  const addToCart = (id) => {
    //console.log(id);
    dispatch({ type: TYPES.ADD_TO_CART, payload: id });
  };

  const delFromCart = (id, all = false) => {
    //console.log(id, all);
    if (all) {
      dispatch({ type: TYPES.REMOVE_ALL_FROM_CART, payload: id });
    } else {
      dispatch({ type: TYPES.REMOVE_ONE_FROM_CART, payload: id });
    }
  };

  const clearCart = () => {
    dispatch({ type: TYPES.CLEAR_CART });
  };

	return (

		<div className="container">

		{shoes.map(item => (
			<div className="card" key={item.id}>
				<div className="left-column background1-left-column">

					<img src={`${process.env.PUBLIC_URL}/img/${item.img}`} alt={item.nombre} width="150px" className="img-fluid img"/>
					<h6>{item.name}</h6>
					<h6>{item.price}</h6>
					<h6>{item.size}</h6>
				</div>

				<div className="right-column">
					<div>
						<h4>Descripci√≥n</h4>

					</div>
					<p>{item.description}</p>
					<button className="button background1-left-column">Empezar</button>
				</div>

			</div>


		))}


	</div>

	);
}

export default Products;
