import "../Pages/Cart/cart.css"

const CartItem = ({ data, delFromCart, addToCart }) => {
    let { id, name, price, size, description, img, quantity } = data;

	const [state, dispatch] = useReducer(shoppingReducer, shoppingInitialState);

	const { cart } = state;
  
    return (
		<div className="container-cart">
      <div className="card-cart" key={id}>
					<div className="left-column-cart background1-left-column-cart">

						<img src={`${process.env.PUBLIC_URL}/img/${img}`} alt={name} width="150px" className="img-fluid img-cart"/>
						<h6>{name}</h6>
						<h6>${price}.00 x {quantity} = ${price * quantity}.00</h6>
						<h6>{size}</h6>
					</div>

					<div className="right-column-cart">
						<div>
							<h4>Descripci√≥n</h4>

						</div>
						<p>{description}</p>
						<div className="container-btn">
						<button onClick={() => delFromCart(id)} className="button-cart background1-left-column-cart "><h1>-</h1></button>
						<button onClick={() => addToCart(id)} className="button background1-left-column"><h1>+</h1></button>
						</div>
					</div>

					{cart.length === 0 && <div>Cart is empty</div>}
    <div>{cart * quantity}</div>

				</div>
				</div>

    );
  };
  
  export default CartItem;
