import { useReducer, useState } from "react";
import "../Products/products.css"
import { Data } from "./Data";
import { useSelector, useDispatch } from "react-redux";


const Products = () => {
  const [state, dispatch] = useReducer(shoppingReducer, shoppingInitialState);

  const { products, cart } = state;

  const addToCart = (id) => {
    dispatch({ type: TYPES.ADD_TO_CART, payload: id });
  };

  const delFromCart = (id, all = false) => {
    if (all) {
      dispatch({ type: TYPES.REMOVE_ALL_FROM_CART, payload: id });
    } else {
      dispatch({ type: TYPES.REMOVE_ONE_FROM_CART, payload: id });
    }
  };

  const allCategories = ['Todos', ...new Set(products.map(product => product.category))];

  const [productItem, setProductItem] = useState(products);
  const [buttons, setButtons] = useState(allCategories);

  const filter = (button) =>{

    if(button === 'Todos'){
      setProductItem(products);
      return;
    }

    const filteredData = products.filter(product => product.category ===  button);
    setProductItem(filteredData)
  }

  return (

    <>
      <h1 className="first-text">PRODUCTOS</h1>
      <Button button={buttons} filter={filter} />
      {Data.map((item) => {
      <div className="container">
				<div className="card" key={id}>
					<div className="left-column background1-left-column">

						<img src={`${process.env.PUBLIC_URL}/img/${img}`} alt={item.name} width="150px" className="img-fluid img"/>
						<h6>{item.name}</h6>
						<h6>${item.price}</h6>
						<h6>{item.size}</h6>
					</div>

					<div className="right-column">
						<div>
							<h4>Descripci√≥n</h4>

						</div>
						<p>{item.description}</p>
						<button onClick={() => dispatch({ type: "ADD", payload: item })} className="button background1-left-column"><h1>+</h1></button>

					</div>

				</div>

		</div>
    })}
    </>

  );
}

export default Products;
